package pkg;

public class Controlstmtif {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=40,b=50,c=40;
		if(a>b && a>c)
		{
		System.out.println("a is greater");	
		}
		else if(b>a && b>c)
		{
			System.out.println("b is greater");
		}
		else
		{
			System.out.println("c is greater");
		}

	}

}
